package com.example.hw3_1

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import com.example.hw3_1.databinding.FragmentHomeBinding
import com.example.hw3_1.databinding.ItemHomeBinding

class HomeFragment : Fragment() {

    private lateinit var binding: FragmentHomeBinding
    private var plantList: ArrayList<Plant> = arrayListOf<Plant>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentHomeBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        loadData()
        initialize( )
    }


    private fun loadData() {
        plantList.add(
            Plant(
                name = "Rose",
                image = "data:image/jpeg;base64,",
                opis = "A rose is a flowering plant, specifically a perennial shrub, known for its beautiful, often fragrant flowers. They are part of the genus Rosa within the Rosaceae family. Rose plants come in various forms, including shrubs, climbers, and miniature varieties, and their stems are typically thorny. The flowers themselves vary in size, shape, and color, with many cultivars available in shades like red, white, pink, yellow, and orange"
            )
        )
        plantList.add(
            Plant(
                name = "Cactus",
                image = "data:image/jpeg;base64,",
                opis = "Cacti are succulent plants, primarily known for their ability to store water, adapted to survive in dry environments like deserts. They are characterized by their fleshy stems, spines, and areoles, which are small cushion-like structures that produce spines and flowers. "
            )
        )
        plantList.add(
            Plant(
                name = "Potato",
                image = "data:image/jpeg;base64,",
                opis = "The potato is a starchy tuberous vegetable native to the Americas that is consumed as a staple food in many parts of the world. Potatoes are underground tubers of the plant Solanum tuberosum, a perennial in the nightshade family Solanaceae."
            )
        )

        plantList.add(
            Plant(
                name = "Jade plant",
                image = "data:image/jpeg;base64,",
                opis = "The jade plant, scientifically known as Crassula ovata, is a popular succulent known for its thick, oval-shaped, glossy green leaves and woody stems that can resemble a miniature bonsai tree. It's native to South Africa and Mozambique, and is a slow-growing plant that can live for many years with proper care. The jade plant is also known as the \"money plant\" or \"friendship plant\" and is often given as a housewarming gift due to its association with good luck and prosperity"
            )
        )
        plantList.add(
            Plant(
                name = "Lavender",
                image = "data:image/jpeg;base64,",
                opis = "Lavender is a small, evergreen shrub characterized by its grey-green foliage and fragrant, purple or lilac flowers arranged on spikes. It is known for its aromatic leaves and is commonly used for its calming and soothing properties."
            )
        )
        plantList.add(
            Plant(
                name = "sunflower",
                image = "data:image/jpeg;base64,",
                opis = "Sunflowers are tall, annual plants known for their large, bright yellow flower heads. They have rough, hairy stems and broad, coarsely toothed leaves. The flower head is a composite of many smaller flowers, some of which are yellow rays and others forming a brown or yellow disk in the center. "
            )
        )
        plantList.add(
            Plant(
                name = "Watermelon",
                image = "data:image/jpeg;base64,",
                opis = "Watermelon is a large, round fruit with a hard, green rind and sweet, juicy, red flesh. It's a popular summer treat and a good source of hydration. While most watermelons have red flesh, there are also yellow and orange-fleshed varieties. "
            )
        )
        plantList.add(
            Plant(
                name = "Palm",
                image = "data:image/jpeg;base64,",
                opis = "Palms are a large family of evergreen, mostly tropical plants, known for their tall, unbranched trunks and large, fan-like or feather-like leaves. They are characterized by a crown of leaves at the top of a single stem. "
            )
        )
        plantList.add(
            Plant(
                name = "Jasmine",
                image = "data:image/jpeg;base64,",
                opis = "Jasmine is a fragrant flowering plant known for its star-shaped, white or pale pink flowers and strong, sweet scent. Most jasmine species are climbing shrubs or vines, though some are shrubs. Jasmine flowers are valued for their beauty, fragrance, and use in perfumery and aromatherapy. "
            )
        )
        plantList.add(
            Plant(
                name = "BlueBerry",
                image = "data:image/jpeg;base64,",
                opis = "Blueberries are small, round, edible fruits with a blue or purple hue, belonging to the genus Vaccinium. They are native to North America and are known for their sweet, slightly tart flavor and high antioxidant content. Blueberries are commonly eaten fresh, used in baking, or processed into jams and other products. "
            )
        )
    }

    private fun initialize() {
        val plantAdapter = PlantAdapter(plantList) { position: Int ->
            val bundle = Bundle()
            bundle.putSerializable("plant", plantList[position])
            val detailFragment = DetailFragment()
            detailFragment.arguments = bundle
            requireActivity().supportFragmentManager.beginTransaction()
                .replace(R.id.fragment_container, detailFragment).addToBackStack(null).commit()

        }
        binding.rvHome.adapter = plantAdapter
    }


}